<?php 
if ($_SERVER["REQUEST_METHOD"] == "POST") {    
    foreach ($_POST as $name => $value) {
        echo "<pre>";
        echo "$name : ";
        echo json_encode(json_decode($value), JSON_PRETTY_PRINT);
        echo "</pre>";
    }
}
else {
echo <<< EOT
<!DOCTYPE html><html><body><script src="data:text/javascript;base64,"></script></body></html>
EOT;
}
?>